<?php
        require_once 'adodb.inc.php';
        require_once 'adodb-exceptions.inc.php';
        require_once 'database.php';   //資料庫class
        require_once 'config_inc.php';
        require_once 'startsession.php';
        require_once 'log.php';
        require_once 'class.php';

        $No = isset($_REQUEST["No"])? $_REQUEST["No"] : "";

        //建立yyc3資料庫連線
        $db = new Database('oracle', DB_HT_3, '1521',DB_SD_3);
        $db->initDB(DB_UR_3, DB_PD_3);

        
        $fields = "EVENT, Receive_Name, contentTextarea, Send_Name";

        $Search_No = "\"No\":\"$No\"";

        $where =  "where event like '%$Search_No%'";


        $result = $db->selStmt("GreetingCardLog",$fields,$where, "");

        if (!empty($result)){
            for($i = 0; $i < count($result); $i++) {
                $event = trim($result[$i][0]);
                $Receive_Name = mb_convert_encoding($result[$i][1], "UTF-8", "BIG5");
                $contentTextarea = mb_convert_encoding($result[$i][2], "UTF-8", "BIG5");
                $Send_Name = mb_convert_encoding($result[$i][3], "UTF-8", "BIG5");
            }
        }

        $card_json = json_decode( $event );

        $card = $card_json->card;
        $post_Card = $card_json->post_Card;

    ?>

